import React, { useEffect, useState  } from 'react';
import './Journalism.scss';
import sanityClient from '../../client'
import BlockContent from '@sanity/block-content-to-react'


import PageContainer from '../../components/PageContainer/PageContainer';
import WritingItem from '../../components/WritingItem/WritingItem';

import JOURNALISM from '../../constants/journalism';

const Journalism = () => {
  const [allPostsData, setAllPosts] = useState(null)

  useEffect(() => {
    sanityClient
      .fetch(
        // `*[_type == 'work' &&
        //     *[_type == "category" &&
        //       title == "journalism"][0]._id in category[]._ref]{
        //         body,
        //         title,
        //         slug,
        // }`
        `*[_type == 'work']{
                  title,
                  link,
                  publication,
                  year
        }`
      )
      .then((data) => setAllPosts(data))
      .catch(console.error);
  }, []);

  console.log({allPostsData})

  return (
    <PageContainer className="journalism">
      {JOURNALISM.map((p) =>
        <WritingItem
          writing={p}
        />
      )}
      {/* {allPostsData ?
        allPostsData.map((p,i) => (
          <div key={i}>{p.title}</div>
        ))
      : null} */}
    </PageContainer>
  )

}

export default Journalism;
