import React, { useEffect, useState  } from 'react';
import './Journalism.scss';
import sanityClient from '../../client'

import PageContainer from '../../components/PageContainer/PageContainer';
import WritingItem from '../../components/WritingItem/WritingItem';

import JOURNALISM from '../../constants/journalism';

const Journalism = () => {
  const [journalismWorks, setJournalismWorks] = useState(null)

  useEffect(() => {
    sanityClient
      .fetch(
        // `*[_type == 'work' &&
        //     *[_type == "category" &&
        //       title == "journalism"][0]._id in category[]._ref]{
        //         body,
        //         title,
        //         slug,
        // }`
        `
          *[_type == "work" && [_type == "category" && title == "Journalism"]]{
            title,
            link,
            year,
            description
          }
        `

        // `*[_type == 'publication']{
        //     title,
        //     "works": *[_type == 'work' && references(^._id)]{
        //       title,
        //       link,
        //       year,
        //       description,
        //     }
        // }`
      )
      .then((data) => setJournalismWorks(data))
      .catch(console.error);
  }, []);

  console.log({journalismWorks})

  return (
    <PageContainer className="journalism">

      {journalismWorks ?
        journalismWorks.map((p, i) =>
          <WritingItem
            key={i}
            writing={p}
          />
        )
      : null}

    </PageContainer>
  )

}

export default Journalism;
