import React, { useEffect, useState  } from 'react';
import './Journalism.scss';
import sanityClient from '../../client'

import PageContainer from '../../components/PageContainer/PageContainer';
import WritingItem from '../../components/WritingItem/WritingItem';

import JOURNALISM from '../../constants/journalism';

const Journalism = () => {
  const [allPostsData, setAllPosts] = useState(null)

  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "category" && categories == ["Journalism"]]{
          _id,
          _type,
          title,
          "posts": *[_type == "post" && references(^._id)]{
            title,
            "author": *[_type=="author"]{
              name
            }
          }
        }`
      )
      .then((data) => setAllPosts(data))
      .catch(console.error);
  }, []);

  console.log({allPostsData})

  return (
    <PageContainer className="journalism">
      {JOURNALISM.map((p) =>
        <WritingItem
          writing={p}
        />
      )}
      {allPostsData ?
        allPostsData.map((c,i) => (
          <>
          {c.title === "Journalism" ?
          <>
            <div key={i}>{c.title}</div>
            {c.posts.map((p,i) =>
              <div key={i}>
                <span>{p.title}</span>
                <span>{p.author[0].name}</span>
              </div>
            )}
          </>
          : null}
          </>
        ))
      : null}
    </PageContainer>
  )

}

export default Journalism;
